const json = $input.first().json;
let existingUrls;

if (Array.isArray(json)) {
  existingUrls = json;
} else if (typeof json.data === 'string') {
  try { existingUrls = JSON.parse(json.data); } catch (e) { existingUrls = []; }
} else {
  existingUrls = [];
}

if (!Array.isArray(existingUrls)) existingUrls = [];
return [{json: { existingUrls }}];

const item = $input.first().json;
const source = $('Trigger').first().json.source || 'unknown';
let statusCode = 'N/A';
let code = 'UNKNOWN';
let message = 'Unexpected error';

if (item.statusCode) statusCode = String(item.statusCode);
if (item.body?.code) code = item.body.code;
if (item.body?.message) message = item.body.message;

const raw = item.error?.message || '';
if (raw && code === 'UNKNOWN') {
  const statusMatch = raw.match(/^(\d{3})/);
  if (statusMatch) statusCode = statusMatch[1];
  const jsonMatch = raw.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    try {
      const parsed = JSON.parse(jsonMatch[0].replace(/\\"/g, '"'));
      code = parsed.code || code;
      message = parsed.message || message;
    } catch (e) { message = raw; }
  } else { message = raw; }
}

return [{json: {
  level: 'error',
  message: `POST /jobs/check-urls: HTTP ${statusCode} [${code}] \u2014 ${message}`,
  source
}}];
