const { categories, locations, remoteOnly } = $input.first().json;

if (!categories || categories.length === 0) {
  return [];
}

return categories.map(cat => ({
  json: { category: cat, locations: locations || [], remoteOnly: remoteOnly || false }
}));

const item = $input.first().json;
const source = $('Trigger').first().json.source || 'unknown';
let statusCode = 'N/A';
let code = 'UNKNOWN';
let message = 'Unexpected error';

if (item.statusCode) statusCode = String(item.statusCode);
if (item.body?.code) code = item.body.code;
if (item.body?.message) message = item.body.message;

const raw = item.error?.message || '';
if (raw && code === 'UNKNOWN') {
  const statusMatch = raw.match(/^(\d{3})/);
  if (statusMatch) statusCode = statusMatch[1];

  const jsonMatch = raw.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    const jsonStr = jsonMatch[0].replace(/\\"/g, '"').replace(/\\\\/g, '\\');
    try {
      const parsed = JSON.parse(jsonStr);
      code = parsed.code || code;
      message = parsed.message || message;
    } catch (e) {
      message = raw.replace(/^\d{3}\s*-\s*/, '').replace(/^"|"$/g, '').replace(/\\"/g, '"');
    }
  } else {
    message = raw;
  }
}

return [{json: {
  level: 'error',
  message: `GET /criteria: HTTP ${statusCode} [${code}] \u2014 ${message}`,
  source
}}];
