const staticData = $getWorkflowStaticData('global');
const now = Date.now();
const maxRuntime = 45 * 60 * 1000;

if (staticData.isRunning && (now - staticData.startedAt) < maxRuntime) {
  return [];
}

staticData.isRunning = true;
staticData.startedAt = now;
return [{json: {source: 'linkedin'}}];

const proxiesData = $('Get Proxies').first().json;
const proxies = proxiesData.proxies;
const proxyList = proxiesData.proxyList;

const criteriaItem = $input.first().json;
const locations = criteriaItem.locations || [];
const isRemote = criteriaItem.remoteOnly ?? true;
const categories = $input.all().map(i => i.json.category);
const queries = [];

for (const searchTerm of categories) {
  for (const location of locations) {
    queries.push({ json: { searchTerm, location, isRemote, proxies, proxyList } });
  }
}

return queries;

const source = $('Check Running').first()?.json?.source || 'linkedin';
const seen = new Set();
const allJobs = [];

for (const item of $input.all()) {
  const job = item.json;
  const url = job.job_url;
  if (!url || seen.has(url)) continue;
  seen.add(url);

  let salary = null;
  if (job.min_amount && job.max_amount) {
    salary = `${job.min_amount}-${job.max_amount} ${job.currency || 'USD'}`;
  } else if (job.min_amount) {
    salary = `${job.min_amount}+ ${job.currency || 'USD'}`;
  }

  allJobs.push({
    title: job.title || '',
    company: job.company || null,
    url,
    salary,
    location: job.location || null,
    publishedAt: job.date_posted || null,
    rawData: job,
  });
}

const proxyList = $('Build Queries').first().json.proxyList;
const urls = JSON.stringify(allJobs.map(j => j.url));

return [{json: { allJobs, proxyList, totalFound: allJobs.length, urls, source }}];

const { allJobs, proxyList, totalFound, source } = $('Collect & Dedup').first().json;
const existingUrls = $input.first().json.existingUrls || [];

const existingSet = new Set(existingUrls);
const newJobs = allJobs.filter(j => !existingSet.has(j.url));

return [{json: {
  newJobs,
  proxyList,
  totalFound,
  newCount: newJobs.length,
  existingCount: existingUrls.length,
  source,
}}];

const { newJobs, totalFound, source } = $('Filter New').first().json;
const enrichResponse = $input.first().json;
const enrichResults = enrichResponse.results || [];
const enrichStats = enrichResponse.stats || {};

const enrichedByUrl = {};
for (const r of enrichResults) {
  if (r.status === 'success' && r.data) {
    enrichedByUrl[r.url] = r.data;
  }
}

const ingestBody = newJobs
  .filter(job => enrichedByUrl[job.url])
  .map(job => {
    const e = enrichedByUrl[job.url];
    return {
      title: job.title,
      company: e.company_name || job.company,
      url: job.url,
      description: e.description || '',
      source: 'LINKEDIN',
      salary: e.salary || job.salary,
      location: e.location || job.location,
      remote: job.remote,
      publishedAt: e.published_at || job.publishedAt,
      rawData: { ...job.rawData, ...e },
    };
  });

const enrichedCount = Object.keys(enrichedByUrl).length;
const skippedCount = newJobs.length - ingestBody.length;

return [{json: {
  body: ingestBody,
  count: ingestBody.length,
  enrichStats,
  enrichedCount,
  skippedCount,
  totalFound,
  source,
}}];

const data = $('Build Ingest').first().json;
const source = data.source || 'linkedin';
const stats = data.enrichStats;
const enriched = data.enrichedCount;
const total = data.count;
const found = data.totalFound;
const skippedIngest = data.skippedCount || 0;
const duration = stats.duration_seconds || 0;

let level = 'info';
let msg = `${found} found, ${total} new, ${enriched} enriched`;

// Failed jobs breakdown
const failed = stats.failed || {};
const failedEntries = Object.entries(failed);
const failedTotal = failedEntries.reduce((s, [, v]) => s + v, 0);

if (failedEntries.length > 0) {
  const reasons = failedEntries.map(([k, v]) => `${v}\u00d7 ${k}`).join(', ');
  msg += `\n\u274c ${failedTotal} failed: ${reasons}`;
}

if (skippedIngest > 0) {
  msg += `\n\u26a0\ufe0f ${skippedIngest} not ingested (no enrichment data)`;
}

msg += ` | \u23f1 ${duration}s`;

// Proxy deaths (dead: {"host:port": "reason"})
const proxies = stats.proxies || {};
const proxyDead = proxies.dead || {};
const deadEntries = Object.entries(proxyDead);
if (deadEntries.length > 0) {
  const alive = proxies.alive || 0;
  const deadTotal = deadEntries.length;
  const lines = deadEntries.map(([host, reason]) => `  \u2022 ${host} \u2192 ${reason}`);
  msg += `\n\U0001f50c Proxies: ${alive}/${alive + deadTotal} alive\n${lines.join('\n')}`;
}

if (failedTotal > 0 && enriched === 0) {
  level = 'error';
} else if (deadEntries.length > 0 || failedTotal > 0) {
  level = 'warn';
}

return [{json: { level, message: msg, source }}];

const { totalFound, existingCount, source } = $('Filter New').first().json;
return [{json: {
  level: 'info',
  message: `${totalFound} found, 0 new (${existingCount} already in DB)`,
  source
}}];

const err = $input.first().json;
const source = $('Check Running').first()?.json?.source || 'linkedin';
const raw = err.error?.message || err.message || err.errorMessage || JSON.stringify(err).substring(0, 200);

let msg = raw;
const timeoutMatch = msg.match(/timeout of (\d+)ms exceeded/);
if (timeoutMatch) {
  const seconds = Math.round(parseInt(timeoutMatch[1]) / 1000);
  msg = `Request timeout after ${seconds}s`;
}

return [{json: {level: 'error', message: msg, source}}];
